msg.tab_id_z = RED.util.generateId();

var subflowId = RED.util.generateId();

var getName = () => {
    var pl = msg.payload;
    /* 
     * This supports the title being (in that order):
     *    - payload.rss.channel[0].title[0]
     *    - payload.feed.title[0]._ 
     *    - payload.feed.title[0]
     */
    return ( 
        (pl && pl.rss && pl.rss.channel && pl.rss.channel[0] && pl.rss.channel[0].title && pl.rss.channel[0].title[0]) ||
        (pl && pl.feed && pl.feed.title && pl.feed.title[0] && (pl.feed.title[0]._ || pl.feed.title[0]) ) ||
        "Unk: " + subflowId
    )
};

var subflowDefined = flow.get(msg.node_colour.substr(1,12));

