var children = payload.nodes;
var fixedNodeId = payload.fixedNodeId;

var changedNodes = [];

// Before moving anything we get the offset (x,y) - this is the amount that our
// fixed Node moved - our fixed node does not move, this means everything is offset
// by the distance it moved.
var offsetX = 0;
var offsetY = 0;

children.forEach((c) => {
    if (c.id == fixedNodeId) {
        var nd = RED.nodes.node(c.id);
        offsetX = c.x - nd.x;
        offsetY = c.y - nd.y;
    }
});

if (isNaN(offsetY) || isNaN(offsetX)) {
    console.log(fixedNodeId)
    console.log(RED.nodes.node(fixedNodeId))
    console.log(children)
    node.send({ type: "error", payload: "Offset is NaN: " + fixedNodeId})
} else {
    children.forEach((c) => {
        var nd = RED.nodes.node(c.id);

        changedNodes.push({
            n: nd,
            ox: nd.x,
            oy: nd.y,
            moved: nd.moved
        });

        if (!isNaN(c.x - offsetX) && !isNaN(c.y - offsetY)) {
            nd.x = c.x - offsetX;
            nd.y = c.y - offsetY;
            nd.dirty = true;
        }
    });

    RED.history.push({ t: "move", nodes: changedNodes, dirty: RED.nodes.dirty() });
    RED.nodes.dirty(true);
    RED.view.redraw(true);

    node.send({ type: "ok", payload: payload })
}

