// see https://github.com/kieler/elkjs#api for more details
var graph = {
    id: "root",
    layoutOptions: {
        'algorithm': 'org.eclipse.elk.box',
        'childAreaHeight': 3000,
        'childAreaWidth': 3000,
    },
    children: allnodes,
    edges: alledges
};

const elk = new ELK();

elk.layout(graph)
    .then((g) => {
        node.send({
            payload: {
                fixedNodeId: allnodes[0].id,
                nodes: g.children
            }
        })
    })
    .catch((g) => { node.send({ type: "error", payload: g }) });

