/**
 * Msg attributes:
 *   - owner: github repo owner
 *   - repo: github repo name
 *   - payload: sha of tree (i.e. directory)
 *   - recursive: if set to a true value, entire directory tree is retrieved.
 * 
 * Return:
 *   - payload: tree of the sha, this will be an array with one entry per member of tree
 */

try {

    octokit.request("GET /repos/{owner}/{repo}/git/trees/{tree_sha}" + (msg.recursive ? "?recursive=true" : ""), {
        owner: msg.owner,
        repo: msg.repo,
        tree_sha: msg.payload
    }).then( (resp) => {
        node.send({ ...msg, payload: resp.data.tree})
    }).catch(function (e) {
         node.error("obtaining tree", { ...msg, error: e })
    });

} catch (e) {
    node.error("something went wrong", { ...msg, error: e})
}

