/**
 * Msg attributes:
 *   - owner: github repo owner
 *   - repo: github repo name
 *   - payload: array of blobs, each blob is an object with:
 *         - path: filename of the blob in the repo, with path, i.e., dir1/dir2/filename.txt
 *         - sha: the sha of the blob created initially using the create blob function
 *   - parent_sha: the current sha of the repo, the return value of current repo revision
 * 
 * Return:
 *   - payload: sha of newly created tree
 */

try {
    var tree = msg.payload.map(function(blb){
        return {
            ...blb,
            mode: "100644",
            type: "blob"
        }
    });

    octokit.request("POST /repos/:owner/:repo/git/trees", {
        owner: msg.owner,
        repo: msg.repo,
        base_tree: msg.parent_sha,
        tree: tree
    }).then((resp) => {
        node.send({ ...msg, payload: resp.data.sha })
    }).catch(function (e) {
        node.error("creating commit", { ...msg, error: e })
    });

} catch (e) {
    node.error("something went wrong", { ...msg, error: e})
}

