/**
 * Msg attributes:
 *   - owner: github repo owner
 * 
 * Return:
 *   - ratelimits in the header
 * 
 * Source: https://docs.github.com/en/rest/overview/resources-in-the-rest-api?apiVersion=2022-11-28#rate-limit-headers
 */

try {

    octokit.request("GET /rate_limit", {
        owner: msg.owner,
    }).then((resp) => {
        node.send({
            ...msg, payload: Object.keys(resp.headers).filter((d) => {
                return d.startsWith("x-ratelimit")
            }).map((k) => {
                return [k, resp.headers[k]]
            }).reduce((o, c) => {
                o[c[0]] = c[1]
                return o
            }, {})
        })
    }).catch(function (e) {
        node.error("obtaining commits", { ...msg, error: e })
    });

} catch (e) {
    node.error("request failure", { ...msg, error: e })
}

