/**
 * Msg attributes:
 *   - owner: github repo owner
 *   - repo: github repo name
 *   - payload: base64 encoded content for blob
 * 
 * Return:
 *   - payload: sha of newly created blob
 */

try {

    octokit.request("POST /repos/:owner/:repo/git/blobs", {
        owner: msg.owner,
        repo: msg.repo,
        content: msg.payload,
        encoding: "base64"
    }).then( (resp) => {
        node.send({ ...msg, payload: resp.data.sha})
    }).catch(function (e) {
        node.error("creating blob", { ...msg, error: e })
    });

} catch (e) {
    node.error("something went wrong", { ...msg, error: e})
}

