    octokit.request("GET /repos/:owner/:repo/git/commits/:sha", {
        owner: msg.owner,
        repo: msg.repo,
        sha: msg.payload,
    }).then( (resp) => {
        node.send({ ...msg, payload: resp.data })
    }).catch(function (e) {
        node.error("obtaining commits", { ...msg, error: e })
    });

} catch (e) {
    node.error("request failure", { ...msg, error: e})
}

