return {
    ...msg,
    col: parseInt( msg.req.params.col ),
    row: parseInt(msg.req.params.row),
    payload: msg.req.params.action == "on"
}

msg.statusCode = 404;
msg.payload = {
    status: "not_found",
    msg: "page not found",
    path: msg.req.originalUrl
};
return msg;

return {
    ...msg,
    col: parseInt( msg.req.params.col ),
    row: 0,
    payload: msg.req.params.action == "on"
}

// if col or row is zero, then all leds in a row or a col respectively is meant
if ( msg.col == 0 && msg.row == 0 ) {
    if ( msg.payload ){
        msg.payload = {
            status: "ok",
            action: "all-on"
        }
        node.send([undefined, msg, undefined])
    } else {
        msg.payload = {
            status: "ok",
            action: "all-off"
        }
        node.send([msg, undefined, undefined])
    }

    return undefined;
}

if ( msg.col == 0 ) {
    for ( var idx = 1; idx < 12; idx++ ) {
        node.send([undefined,undefined, {
            ...msg,
            col: idx
        }])
    }
    return undefined;
}


if (msg.row == 0) {
    for (var idx = 1; idx < 6; idx++) {
        node.send([undefined, undefined, {
            ...msg,
            row: idx
        }])
    }
    return undefined;
}


return [undefined,undefined,msg];
