var line = msg.payload;

msg.payload = {
    icao: line.substring(20,24).trim(),
    lat: line.substring(39,45).trim(),
    lon: line.substring(47,54).trim(),
}

if ( msg.payload.icao.trim().match(/^[A-Z]{4}$/) ) {
    var mtch = msg.payload.lat.match(/^([0-9]{2,3}) ([0-9]{2,3})([A-Z])$/);
    if ( mtch ) {

        msg.payload.lat = geolib.toDecimal(`${mtch[1]}° ${mtch[2]}' 0" ${mtch[3]}`);

        mtch = msg.payload.lon.match(/^([0-9]{2,3}) ([0-9]{2,3})([A-Z])$/);
        if ( mtch ) {
            msg.payload.lon = geolib.toDecimal(`${mtch[1]}° ${mtch[2]}' 0" ${mtch[3]}`);

            var pt = {
                latitude: msg.payload.lat,
                longitude: msg.payload.lon
            };

            if ( geolib.isPointWithinRadius(pt,msg.myposition,msg.radius_meters) ) {
                msg.url = `https://www.aviationweather.gov/adds/dataserver_current/httpparam?dataSource=metars&requestType=retrieve&format=xml&stationString=${msg.payload.icao}&hoursBeforeNow=24`
                return msg
            } else {
                msg.payload = false;
                return msg;
            }
        }
    }
}

msg.payload = false;
return msg;

msg.payload = msg.payload.filter( (obj) =>{
    return !!obj;
})

msg.closest = [...msg.payload];
msg.closest.sort( function(a,b) {
    return (a.distance < b.distance) ? -1 : 1
});
msg.closest.length = 30;

msg.warmest = [...msg.payload];
msg.warmest.sort(function (a, b) {
    return (a.temperature.avg < b.temperature.avg) ? 1 : -1
});
msg.warmest.length = 30;

msg.payload = [
    ...msg.warmest,
    ...msg.closest
];

return msg;

if ( parseInt(msg.payload.details.response.data[0].$.num_results) == 0 ) {
    msg.payload = false;
    return msg;
} 

var temps = [];

var data = msg.payload.details.response.data[0].METAR;

for ( var idx = 0 ; idx < data.length ; idx++ ) {
    temps.push(parseFloat(data[idx].temp_c || 0) )    
}

msg.payload = {
    ...msg.payload,
    temperature: {
        max: Math.max(...temps),
        min: Math.min(...temps),
        avg: temps.reduce((p, d) => { return p + d }, 0) / temps.length
    }
}

if (msg.payload.temperature.max < 20 && msg.payload.temperature.avg < 15 ) {
    msg.payload = false;
    return msg;
}

var stationLoc = {
    latitude: msg.payload.station.lat,
    longitude: msg.payload.station.lon
};

msg.payload = {
    ...msg.payload,
    distance: geolib.getDistance(msg.myposition, stationLoc, 1)
};

return msg;

const overpassClient = new overpassFrontend('//overpass-api.de/api/interpreter');

msg.orig_payload = { ...msg.payload};

overpassClient.BBoxQuery(
    msg.payload.osm.query,
    msg.payload.osm.bbox,
    {
        properties: overpassFrontend.TAGS,
        limit: 5,
        sort: true
    },

    function (err, result) {
        if (result) {
            node.send({ ...msg, payload: result})
        } else {
            node.send({ ...msg, payload: undefined} )
        }
    },

    function (err) {
        if (err) { 
            node.error(err) 
        } else {
            node.send({ 
                ...msg, 
                complete: true, 
                payload: undefined 
            })
        }
    }
)

msg.payload.station.boundingbox = geolib.getBoundsOfDistance({
    latitude: msg.payload.station.lat,
    longitude: msg.payload.station.lon,
}, 3000);

msg.payload.osm = {
    bbox: {
        minlat: msg.payload.station.boundingbox[0].latitude,
        minlon: msg.payload.station.boundingbox[0].longitude,
        maxlat: msg.payload.station.boundingbox[1].latitude,
        maxlon: msg.payload.station.boundingbox[1].longitude,
    },
    query: "(node[place=town];node[place=city];)"
}

return msg;

msg.payload = {
    town: { ...msg.payload.tags },
    loc: { ...msg.payload.center },
    temp: { ...msg.orig_payload.temperature },
    station: { ...msg.orig_payload.station }
};

return msg;
