var context = window.audiocontext || new AudioContext();
window.audiocontext = context;

var frequency = parseInt(msg.frequency, 10) || node.frequency || 440;
var duration = parseInt(msg.duration, 10);
if (duration != 0 && !duration) {
    duration = node.duration || 500;
}

if (duration > 0) {
    var oscillator = context.createOscillator();

    oscillator.addEventListener("ended", () => {
        node.send({ payload: "stop" })
    })

    oscillator.frequency.value = frequency;

    if ( msg.shape == 'custom' ) {
        var real = new Float32Array(msg.realTable);
        var imag;
        if (msg.imagTable && Array.isArray(msg.imagTable)) {
            imag = new Float32Array(msg.imagTable);
        } else {
            imag = new Float32Array(real.length);
        }
        var pWave = context.createPeriodicWave(real, imag);
        oscillator.setPeriodicWave(pWave);
        //oscillator.type = 'custom'
    } else {
        oscillator.type = msg.shape || node.shape || 'sine';
    }

    let g = new GainNode(context);
    let vol = msg.volume || 1

    oscillator.connect(g).connect(context.destination);

    let startTime = context.currentTime

    g.gain.setValueAtTime(0, startTime);
    g.gain.linearRampToValueAtTime(vol, startTime + ((duration/1000) * 0.5));

    setTimeout( () => {
        node.send({ payload: "start" })
    }, 10)
    oscillator.start(startTime);

    setTimeout(function () {
        let stopTime = context.currentTime + 1;
        g.gain.setValueAtTime(vol, stopTime - 0.5);
        g.gain.linearRampToValueAtTime(0, stopTime);
        oscillator.stop(stopTime);

        setTimeout(() => { oscillator = undefined }, 1100)
    }, duration);
}

