    (function () {

        var atobUtf8 = (content) =&gt; {
            function base64ToBytes(base64) {
              const binString = atob(base64);
              return Uint8Array.from(binString, (m) =&gt; m.codePointAt(0));
            }
            return new TextDecoder().decode(base64ToBytes(content));
        };

