        var d = Date.parse(module.updated_at);
        return ( monthsAgo[y * 12] > d ) && ( d > monthsAgo[(y+1)*12] )
    }).forEach( (module) => {
        var version = msg.versions[module.id]
        if ( version ) {
            (version.maintainers || []).forEach( (mt) => {
                maintainers[y][mt.name] = maintainers[y][mt.name] || {}
                maintainers[y][mt.name][module.id] = true
