    "series": kvs.map((v) => { return v[0] }),
    "data": kvs.map((v) => { return [v[1].length] }),
    "labels": ["Last Active - Total: " + Object.keys(allMaintainers).length]
}]

return msg;

var now = new Date()
var allMaintainers = {}
var releaseCounts = {}
var moduleAge = {}
var firstReleaseEver = moment(now).add(30, 'year');

msg.payload.modules.forEach((module) => {
    var version = msg.versions[module.id]

    if (version) {

        releaseCounts[module.id] = Object.keys(version.releases|| {}).length - 2; // -2 because there is "created" and "modified"

        (version.maintainers || []).forEach((mt) => {
            allMaintainers[mt.name] = allMaintainers[mt.name] || {
                started: moment(now).add(30,'year'),
                stopped: -1
            }

            let latestRelease = version.releases[version.disttags && version.disttags.latest]
            let firstRelease = Date.parse(version.releases["created"])

            firstReleaseEver = Math.min(firstReleaseEver, firstRelease)

            allMaintainers[mt.name].started = Math.min(allMaintainers[mt.name].started, firstRelease)
            if ( latestRelease) {
                moduleAge[module.id] = Date.parse(latestRelease) - firstRelease
                allMaintainers[mt.name].stopped = Math.max(allMaintainers[mt.name].stopped, Date.parse(latestRelease))
            }
        })
    }
});

var packAge = Object.values(moduleAge).filter( d => {
    return d > 0
}).map( d => {
    return moment.duration(d, 'milliseconds').asMonths();
})

var data = Object.values(allMaintainers).filter( d => {
    return d.started < now && d.stopped > 0 && d.started < d.stopped 
}).map( d => {
    return moment.duration(d.stopped - d.started, 'milliseconds').asMonths();
})

var rCnt = Object.values(releaseCounts).filter( d => {
    return d > 0
})

var relPercentiles = [...new Array(10)].map((i, idx) => {
    return [(10*idx+1), jstat.percentile(rCnt, (10*idx+1) / 100)]
});

var actPercentiles = [...new Array(10)].map((i, idx) => {
    return [10*idx+1, jstat.percentile(data, (10*idx + 1) / 100)]
});

var nodereddetails = {}
if ( msg.versions["node-red"]) {
    let nrVersions = msg.versions["node-red"];

    if (nrVersions.releases){
        nodereddetails["releases"] = Object.keys(nrVersions.releases).length - 2
        nodereddetails["first_release"] = moment(Date.parse(nrVersions.releases["created"])).format("dddd, MMMM Do YYYY, h:mm:ss a")
    }

    nodereddetails["dwns"] = {
        "lastweek": msg.downloads["node-red"] || -1,
        "lastmonth": msg.downloads_last_month["node-red"] || -1,
        "lastyear": msg.downloads_last_year["node-red"] || -1
    }
}

return [
    {
        ...msg,
        payload: [{
            "series": relPercentiles.map((v) => { return v[0] }),
            "data": relPercentiles.map((v) => { return [v[1]] }),
            "labels": ["Percentile"]
        }]
    },

    {
        ...msg,
        payload: [{
            "series": actPercentiles.map((v) => { return v[0] }),
            "data": actPercentiles.map((v) => { return [v[1]] }),
            "labels": ["Percentile"]
        }]
    },

    {
        ...msg,
        payload: {
            package_age: {
                median: jstat.median(packAge),
                mean: jstat.mean(packAge),
                max: jstat.max(packAge),
                per90: jstat.percentile(packAge, 0.9)
            },
            release_count: {
                median: jstat.median(rCnt),
                mean: jstat.mean(rCnt),
                max: jstat.max(rCnt),
                per90: jstat.percentile(rCnt, 0.9)
            },
            maintainers_stay: {
                median: jstat.median(data),
                mean: jstat.mean(data),
                max: jstat.max(data),
                per90: jstat.percentile(data, 0.9)
            },
            totals: {
                maintainers: Object.keys(allMaintainers).length,
                packages: msg.payload.modules.length,
                first_package_released: moment(firstReleaseEver).format("dddd, MMMM Do YYYY, h:mm:ss a")
            },
            nodered: nodereddetails
        }
    }
]

msg.payload.modules.push({
    id: "node-red",
    version: "3.1.0",
    description: "Low-code programming for event-driven applications",
    updated_at: new Date(),
    types: [],
    keywords: [],
    url: "https://nodered.org"
})

return msg;

msg.downloads = msg.downloads || {};

var module = msg.modules.pop();

/* 
  Url from https://www.edoardoscibona.com/exploring-the-npm-registry-api, possible periods are:
   - last-year
   - last-month
   - last-year
   - last-day
*/
if (module) {
    return [{
        ...msg,
        url: "https://api.npmjs.org/downloads/point/last-year/" + module.id,
        module: module,
        requestTimeout: 2500,
        isempty: msg.downloads[module.id] == undefined
    },
        undefined
    ]
} else {
    return [
        undefined,
        msg
    ]
}

msg.downloads = msg.downloads || {}

if (msg.payload.package) {
    msg.downloads[msg.payload.package] = msg.payload.downloads;

    return [
        msg,
        undefined
    ]
} else {
    return [
        undefined,
        msg
    ]
}

var kvs = Object.keys(msg.downloads).map((k) => { return [k, msg.downloads[k]]}).filter( (d) => { return d[0] != "undefined" })
kvs = kvs.sort( (a,b) => { return a[1] < b[1] ? 1 : -1 })

var orig = [...kvs]

kvs.length = 100;

return [
    {
        ...msg,
        payload: [{
            "series": kvs.map((v) => { return v[0] }),
            "data": kvs.map((v) => { return [v[1]] }),
            "labels": ["Packages"]
        }]
    },
    {
        ...msg,
        payload: kvs.map((v) => {
            var mod = msg.payload.modules.filter((d) => { return d.id == v[0] })[0];
            return {
                "value": v[0],
                "count": parseInt(v[1]),
                "version": mod.version,
                "desc": mod.description,
                "tags": (mod.keywords || []).map(d => { return { tag: d } })
            }
        })
    },
    {
        ...msg,
        payload: orig
    }
]

msg.payload.modules.push({
    id: "node-red",
    version: "3.1.0",
    description: "Low-code programming for event-driven applications",
    updated_at: new Date(),
    types: [],
    keywords: [],
    url: "https://nodered.org"
})

return msg;

var yearPackage = {}

msg.payload.modules.forEach((module) => {
    var version = msg.versions[module.id];
    if (version && version.releases && version.releases.created) {
        var year = moment(version.releases.created).year()
        yearPackage[year] = yearPackage[year] || 0;
        yearPackage[year] += 1
    }
});

var kvs = Object.keys(yearPackage).map( (k) => { return [parseInt(k), yearPackage[k]]} )
kvs = kvs.sort((a, b) => { return a[0] < b[0] ? 1 : -1 })

return {
        ...msg,
        payload: [{
            "series": kvs.map((v) => { return ""+v[0] }),
            "data": kvs.map((v) => { return [v[1]] }),
            "labels": ["Year - Total: " + jstat.sum(kvs.map((v) => { return v[1] }))]
        }]
}

var maintainers = {}

msg.payload.length = 100

msg.payload.forEach( (ary) => {
    let packname = ary[0];
    let version = msg.versions[packname];

    if (version) {
        (version.maintainers || []).forEach((man) => {
            maintainers[man.name] = (maintainers[man.name] || [])
            maintainers[man.name].push( packname )
        })
    }
})

var kvs = Object.keys(maintainers).map((k) => { return [k, maintainers[k].length] })
kvs = kvs.sort((a, b) => { return a[1] < b[1] ? 1 : -1 })

return [
    {
        ...msg,
        payload: [{
            "series": kvs.map((v) => { return v[0] }),
            "data": kvs.map((v) => { return [v[1]] }),
            "labels": ["Maintainer"]
        }]
    },
    {
        ...msg,
        total: Object.keys(maintainers).length,
        payload: kvs.map((v) => {
            return {
                "value": v[0],
                "count": parseInt(v[1]),
                "modules": maintainers[v[0]].map((d) => { return { name: d } }),
            }
        })
    }
]

var noreadmes = msg.payload.modules.filter((module) => {
    if (!msg.versions[module.id] || !!msg.versions[module.id].unpublished) {
        return false
    } else {
        return !msg.versions[module.id].readme
    }
}).map( module => {
    let mod = msg.versions[module.id];
    let dwn = msg.downloads_last_year[module.id]

    return {
        "value": module.id,
        "count": dwn || -1,
        "version": mod.version,
        "desc": mod.description,
        "tags": (mod.keywords || []).map(d => { return { tag: d } }),
        "str": JSON.stringify(mod, null, 2)
    }
})

var unpublished = msg.payload.modules.filter((module) => {
    if (!msg.versions[module.id]) {
        return false
    } else {
        return !!msg.versions[module.id].unpublished
    }
}).map( module => {
    let mod = msg.versions[module.id];
    let dwn = msg.downloads_last_year[module.id]

    return {
        "value": module.id,
        "count": dwn || -1,
        "version": mod.version,
        "desc": mod.description,
        "tags": (mod.keywords || []).map(d => { return { tag: d } })
    }
})

return [
    {
        ...msg,
        totalPackages: noreadmes.length,
        payload: noreadmes
    },
    {
        ...msg,
        totalPackages: unpublished.length,
        payload: unpublished
    }
]
