  "engines": {
    "node": ">=8"
  },

  "node-red" : {
    "version": ">=2.0.0",
    "nodes": {
      "flowtomermaid": "nodes/flow2mermaid.js"
    }
  },

  "description": "Convert current flow tab to Mermaid flow diagram.",
  "repository": {
    "type": "git",
    "url": "git+https://github.com/{{githubowner}}/{{githubrepo}}.git"
  },
  "bugs": {
    "url": "https://github.com/{{githubowner}}/{{githubrepo}}/issues"
  }
}

module.exports = function(RED) {
  function Flow2MermaidFunctionality(config) {
    RED.nodes.createNode(this,config);

    var node = this;
    var cfg = config;

    node.on('close', function() {
      node.status({});
    });

    node.on("input", function(msg, send, done) {
        send(msg);
        done();
    });
  }
  RED.nodes.registerType("Flow2UML", Flow2MermaidFunctionality);
}

