        error: e
    })
    return undefined;
}

/**
 * Msg attributes:
 *   - owner: github repo owner
 *   - repo: github repo name
 *   - payload: the sha of the tree to commit
 *   - parent_sha: the current sha of the repo, the return value of current repo revision
 *   - author: an object with the following:
 *      - name: name of the author of the commit
 *      - email: email of the author of the commit
 *   - message: commit message
 * 
 * Return:
 *   - payload: sha of newly commit
 * 
 * Source:
 *   - https://docs.github.com/en/rest/git/commits?apiVersion=2022-11-28#create-a-commit
 */

try {

    octokit.request("POST /repos/:owner/:repo/git/commits", {
        owner: msg.owner,
        repo: msg.repo,        
        message: msg.message,
        author: {
            name: msg.author.name,
            email: msg.author.email
        },
        parents: [
            msg.parent_sha
        ],
        tree: msg.payload
    }).then((resp) => {
        node.send({ ...msg, payload: resp.data.sha })
    }).catch(function (e) {
        node.error("creating commit", { ...msg, error: e })
    });

} catch (e) {
    node.error("something went wrong", { ...msg, error: e })
}

try {
    var octokit = new octokitRest.Octokit({
        auth: process.env.GITHUB_TOKEN
    });
} catch (e) {
    node.error("init octokit", {
        ...msg,
        error: e
    })
    return undefined;
}

/**
 * Msg attributes:
 *   - owner: github repo owner
 *   - repo: github repo name
 *   - branch: github repo branch to update with the new commit sha
 *   - payload: the sha of the commit created
 * 
 * Return:
 *   - payload: new sha of the branch
 * 
 * Source:
 *   - https://docs.github.com/en/rest/git/refs?apiVersion=2022-11-28#update-a-reference
 */

try {
    msg.new_commit_sha = msg.payload;

    octokit.request("PATCH /repos/:owner/:repo/git/refs/heads/:branch", {
        owner: msg.owner,
        repo: msg.repo,
        branch: msg.branch,
        sha: msg.payload
    }).then(function (resp) {
        node.send({ 
            ...msg, 
            payload: resp.data.object.sha,
        })
    }).catch(function (e) {
        node.error("committing commit", { ...msg, error: e })
    });

} catch (e) {
    node.error("something went wrong", { ...msg, error: e })
}

