msg.fastmail = {
    ...msg.fastmail,
    account: msg.payload.accounts,
    account_id: Object.keys(msg.payload.accounts)[0]
};

msg.fastmail["uploadUrl"] = msg.payload.uploadUrl.replace( "{accountId}", msg.fastmail.account_id );

msg.headers = {
    "Authorization": "Bearer " + env.get("FASTMAIL_API")
}
msg.url = "https://api.fastmail.com/jmap/api"

msg.payload = {
        "using": [
            "urn:ietf:params:jmap:core",
            "urn:ietf:params:jmap:mail"
        ],
        "methodCalls": [
            [
                "Mailbox/query",
                {
                    "accountId": msg.fastmail.account_id,
                    "filter": { 
                        "name": "Drafts" 
                    }
                },
                "a",
            ]
        ],
    };

return msg;

msg.fastmail = {
    ...msg.fastmail,
    draft_mailbox_id: msg.payload["methodResponses"][0][1]["ids"][0]
};

msg.headers = {
    "Authorization": "Bearer " + env.get("FASTMAIL_API")
}

msg.url = "https://api.fastmail.com/jmap/api"

msg.payload = {
    "using": [
        "urn:ietf:params:jmap:core",
        "urn:ietf:params:jmap:mail",
        "urn:ietf:params:jmap:submission",
    ],
    "methodCalls": [
        [
            "Identity/get",
            {
                "accountId": msg.fastmail.account_id,
            },
            "pluckaduck"
        ]
    ]
};

return msg;

var emailToId = {};
var lst = msg.payload["methodResponses"][0][1]['list'];

for ( var idx = 0 ; idx < lst.length ; idx++ ) {
    var dt = lst[idx];
    emailToId[dt.email] = dt.id;
}

msg.fastmail = {
    ...msg.fastmail,
    emailsToIds: emailToId
};

delete msg.payload;

return msg;

msg.headers = {
    "Authorization": "Bearer " + env.get("FASTMAIL_API")
}
msg.url = "https://api.fastmail.com/.well-known/jmap"

msg.fastmail = {}

return msg;

var to_email   = msg.email.to;
var cc_email   = msg.email.cc;
var from_email = msg.email.from || env.get("SNDML_FROM_EMAIL");
var bcc_email  = msg.email.bcc || env.get("SNDML_BCC_EMAIL");

if ( !from_email ) {
    throw "Missing from email when sending email";
}

var draft_id = "draft_" + RED.util.generateId();

var draft = {
    "from": [
        { 
            "email": from_email, 
            "name": msg.email.from_name || from_email
        }
    ],
    "to": [
        { 
            "email": to_email 
        }
    ],
    "subject": msg.email.subject,
    "keywords": { "$draft": true },
    "mailboxIds": {  },
    "bodyValues": {
        "body": { "value": msg.email.text, "charset": "utf-8" },
        "bodyHTML": { "value": msg.email.html, "charset": "utf-8" }
    },
    "textBody": [{ "partId": "body", "type": "text/plain" }],
    "htmlBody": [{ "partId": "bodyHTML", "type": "text/html" }],
};

var rcptTo = [
    {
        "email": to_email,
        "parameters": null
    }
];

if ( bcc_email && (msg.email.bcc != false)) {
    draft["bcc"] = [{ "email": bcc_email }];
    rcptTo.push({
        "email": bcc_email,
            "parameters": "bcc"
    });
}

if (cc_email && (msg.email.cc != false)) {
    draft["cc"] = [{ "email": cc_email }];
    rcptTo.push({
        "email": cc_email,
        "parameters": "cc"
    });
}

if (msg.email.attachments) {
    draft["attachments"] = [];
    msg.email.attachments.forEach(function(att){
        draft["attachments"].push({
            "blobId": att.blobId,
            "type": att.type,
            "name": att.filename
        });
    });
}

draft["mailboxIds"][msg.fastmail.draft_mailbox_id] = true;

var drafty = {};
drafty[draft_id] = draft;

msg.payload = {
    "using": [
        "urn:ietf:params:jmap:core",
        "urn:ietf:params:jmap:mail",
        "urn:ietf:params:jmap:submission",
    ],
    "methodCalls": [
        [
            "Email/set",
            {
                "accountId": msg.fastmail.account_id,
                "create": drafty
            },
            "a"
        ],
        [
            "EmailSubmission/set",
            {
                "accountId": msg.fastmail.account_id,
                "onSuccessDestroyEmail": ["#sendIt"],
                "create": {
                    "sendIt": {
                        "identityId": msg.fastmail.emailsToIds[from_email],
                        "emailId": "#" + draft_id,
                        "envelope": {
                            "mailFrom": {
                                "email": from_email,
                                "parameters": null
                            },
                            "rcptTo": rcptTo
                        },
                    }
                },
            },
            "b",
        ],
    ],
}

msg.headers = {
    "Authorization": "Bearer " + env.get("FASTMAIL_API")
}

msg.url = "https://api.fastmail.com/jmap/api"

return msg;

if ( msg.statusCode == 200 ) {
    msg.email.attachments = [
        {
            "filename": "attachment.zip",
            "blobId": msg.payload.blobId,
            "type": msg.payload.type
        }
    ]
    return msg;
}

