const extract = tarStream.extract()

var allFiles = [];

var computeFormat = (filename) => {
  var ext = filename.split(".").at(-1);

  return {
    "html": "html",
    "js": "javascript",
    "md": "markdown",
    "json": "json"
  }[ext.toLowerCase()] || "text";
};

extract.on('entry', function (header, stream, next) {
  // header is the tar header
  // stream is the content body (might be an empty stream)
  // call next when you are done with this entry

  var buffer = [];
  stream.on('data', function (data) {
    buffer.push(data)
  });

  stream.on('end', function () {
    allFiles.push({
      id: RED.util.generateId(),
      type: "PkgFile",
      name: header.name.split("/").at(-1),
      filename: header.name.replace(/^package\//,''),
      template: Buffer.concat(buffer).toString('utf8'),
      header: {...header},
      syntax: "mustache",
      format: computeFormat(header.name.split("/").at(-1)),
      output: "str",
      x: 100,
      y: 50 * (allFiles.length+1),
      wires: [
        []
      ]
    })

    next() // ready for next entry
  })

  stream.resume() // just auto drain the stream
})

extract.on('finish', function () {
  // all entries read
  msg.payload = JSON.stringify(allFiles);
  node.send(msg)
  node.done()
})

var stream = streamx.Readable.from(Buffer.from(pakoGzip.inflate(new Uint8Array(msg.payload))))
stream.pipe(extract);
