    allErrors: true,
    messages: true,
    allowUnionTypes: true
})

var validate;
var result;

/* First validate the msg object */

if (msg.msgschema) {
    validate = validator.compile(msg.msgschema)

    result = validate(msg);

    if (!result) {
        msg.errors = validate.errors;
        node.error("msg validation failed", msg)
        node.done();
        return;
    } else {
        delete msg.msgschema;
    }
}

/* validate the environment */

if (msg.envschema) {
    validate = validator.compile(msg.envschema)

    result = validate(process.env);

    if (!result) {
        msg.errors = validate.errors;
        node.error("env validation failed", msg)
        node.done();
        return;
    } else {
        delete msg.envschema;
    }
}

return msg;

