  "author": "Gerrit Riessen <stupidity@spreads-the.love> (https://spread-the.love)",
  "engines": {
    "node": ">=16"
  },

  "node-red" : {
    "version": ">=2.0.0",
    "nodes": {
      "tarball":   "nodes/90-tarball.js"
    }
  },

  "description": "Generate a tarball from a bunch of files.",
  "repository": {
    "type": "github",
    "url": "git+https://github.com/gorenje/node-red-contrib-tarball.git"
  },
  "bugs": {
    "url": "https://github.com/gorenje/node-red-contrib-tarball"
  }
}

module.exports = function (RED) {
    function TarBallFunctionality(config) {
        RED.nodes.createNode(this, config);

        var node = this;
        var cfg = config;

        const tarStream = require('tar-stream');
        const memoryStreams = require('memory-streams');
        const pakoGzip = require('pako')

        node.on('close', function () {
            node.status({});
        });

        node.on("input", function (msg, send, done) {
            const pack = tarStream.pack()

            msg.contents.forEach((elem) => {
                pack.entry({ name: elem.name }, elem.contents)
            })

            pack.finalize()

            var writer = new memoryStreams.WritableStream();

            pack.pipe(writer);

            writer.on('finish', function () {
                msg.payload = Buffer.from(pakoGzip.gzip(writer.toBuffer()));
                send(msg)
                done()
            });
        });
    };

    RED.nodes.registerType("TarBall", TarBallFunctionality);
}
