var data = {
    "client_id":     process.env.LINKEDIN_CLIENT_ID,
    "code":          msg.lnkdCode,
    "client_secret": process.env.LINKEDIN_CLIENT_SECRET,
    "grant_type":    "authorization_code",
    "redirect_uri":  process.env.LINKEDIN_OAUTH_REDIRECT_URL,
};

msg.payload = querystring.stringify(data);

return msg;

msg.access_token = msg.payload.access_token;

msg.headers = {
    "Authorization": "Bearer " + msg.access_token
}
return msg;

msg.user_id = msg.payload.id;
msg.user_urn = "urn:li:person:" + msg.user_id;

msg.headers = {
    "Authorization": "Bearer " + msg.access_token
}

return msg;

msg.blogPageDetailsForPost = undefined;

msg.blogpages.forEach( function(dtls){
    if ( dtls.path == msg.pageData.path ) {
        msg.blogPageDetailsForPost = dtls;
    }
})

if (msg.blogPageDetailsForPost ) { 
    return msg;
}

msg.headers = {
    "Authorization": "Bearer " + msg.access_token,
    "X-Restli-Protocol-Version": "2.0.0"
}

msg.payload = {
    "author": msg.user_urn,
    "lifecycleState": "PUBLISHED", /* for testing DRAFT - but won't find it on LinkedIn */
    "specificContent": {
        "com.linkedin.ugc.ShareContent": {
            "shareCommentary": {
                "text": msg.blogPageDetailsForPost.title +
                    "\n\n" + msg.blogPageDetailsForPost.summary +
                    "\n\n" + (msg.blogPageDetailsForPost.tags || "node-red,visualweb").split(",").map(function (d) { return "#" + d; }).join(",") +
                    "\n\n" + "Great article would scan it again in an instance -- ChatGPT."
            },
            "shareMediaCategory": "ARTICLE",
            "media": [
                {
                    "status": "READY",
                    "description": {
                        "text": msg.blogPageDetailsForPost.summary
                    },
                    "originalUrl": msg.pageData.curl, /* + "?cb=" + RED.util.generateId(), */
                    "title": {
                        "text": msg.blogPageDetailsForPost.title
                    },
                }
            ]
        }
    },
    "visibility": {
        "com.linkedin.ugc.MemberNetworkVisibility": "PUBLIC"
    }
};

if (msg.image_urn) {
    msg.payload.specificContent["com.linkedin.ugc.ShareContent"].media[0]["media"] = msg.image_urn;
    msg.payload.specificContent["com.linkedin.ugc.ShareContent"].shareMediaCategory = "IMAGE";

    msg.payload.specificContent["com.linkedin.ugc.ShareContent"].shareCommentary.text = (
        msg.blogPageDetailsForPost.title +
        "\n\n" + msg.blogPageDetailsForPost.summary +
        "\n\n" + "Link to Post: " + msg.pageData.curl +
        "\n\n" + (msg.blogPageDetailsForPost.tags || "node-red,visualweb").split(",").map(function (d) { return "#" + d; }).join(",") +
        "\n\n" + "Great article would scan it again in an instance -- ChatGPT."
    );
}

msg.url = "https://api.linkedin.com/v2/ugcPosts";

return msg;

msg.headers = {
    "Authorization": "Bearer " + msg.access_token,
    "X-Restli-Protocol-Version": "2.0.0",
    "LinkedIn-Version": "202308"
}

msg.url = "https://api.linkedin.com/v2/assets?action=registerUpload";

msg.payload = {
    "registerUploadRequest": {
        "recipes": [
            "urn:li:digitalmediaRecipe:feedshare-image"
        ],
        "owner": msg.user_urn,
        "serviceRelationships": [
            {
                "relationshipType": "OWNER",
                "identifier": "urn:li:userGeneratedContent"
            }
        ]
    }
}

return msg;

msg.url = msg.payload[1].value.uploadMechanism["com.linkedin.digitalmedia.uploading.MediaUploadHttpRequest"].uploadUrl;
msg.image_urn = msg.payload[1].value.asset;

msg.headers = {
   ...msg.payload[1].value.uploadMechanism["com.linkedin.digitalmedia.uploading.MediaUploadHttpRequest"].headers,
   "Authorization": "Bearer " + msg.access_token
}

msg.payload = msg.payload[0];

return msg;

msg.url = msg.payload[0].value.uploadMechanism["com.linkedin.digitalmedia.uploading.MediaUploadHttpRequest"].uploadUrl;
msg.image_urn = msg.payload[0].value.asset;

msg.headers = {
   ...msg.payload[0].value.uploadMechanism["com.linkedin.digitalmedia.uploading.MediaUploadHttpRequest"].headers,
   "Authorization": "Bearer " + msg.access_token
}

msg.payload = msg.payload[1];

return msg;
