/*
 * Functionality
 */
let ruleJson = (nameStr, data) => {
    return {
        "t": "set",
        "p": nameStr,
        "pt": "msg",
        "to": JSON.stringify(data, null, 2),
        "tot": "json"
    }
}

let ruleString = (nameStr, data) => {
    return {
        "t": "set",
        "p": nameStr,
        "pt": "msg",
        "to": data,
        "tot": "str"
    }
}

let hashKey = (keyStr) => {
    if (keyStr.indexOf("-") > -1) {
        return `["${keyStr}"]`
    } else {
        return `.${keyStr}`
    }
}

/*
 * Templates
 */
let chgTemplate = {
    "type": "change",
    "name": "",
    "action": "",
    "property": "",
    "from": "",
    "to": "",
    "reg": false,
    "x": 0,
    "y": 0,
}

let startHere = {
    ...chgTemplate,
    "id": RED.util.generateId(),
    "name": "Start Here",
    "rules": [ruleJson("payload", {})],
    "wires": [
        []
    ]
}

let lstNde = startHere;
let height = 0;
let nodeCollection = [startHere];

/*
 * Computation starts here.
 */
if (msg.payload.name) {
    startHere.name = msg.payload.name
    startHere.rules.push(ruleString("payload.name", msg.payload.name))
    delete msg.payload.name;
}

if (msg.payload.env) {
    startHere.rules.push(ruleJson("payload.env", {}))

    let envKeys = Object.keys(msg.payload.env)
    for (var kdx = 0; kdx < envKeys.length; kdx++) {
        startHere.rules.push(ruleJson("payload.env" + hashKey(envKeys[kdx]), msg.payload.env[envKeys[kdx]]))
    }

    delete msg.payload.env;
}

['on', 'permissions'].forEach(k => {
    if (Object.keys(msg.payload).indexOf(k) > -1) {
        startHere.rules.push(ruleJson("payload" + hashKey(k), msg.payload[k]))
        delete msg.payload[k];
    }
})

if (msg.payload.jobs) {
    startHere.rules.push(ruleJson("payload.jobs", {}))

    let jobKeys = Object.keys(msg.payload.jobs);

    for (var idx = 0; idx < jobKeys.length; idx++) {
        let ndeId = RED.util.generateId()
        let jobObj = msg.payload.jobs[jobKeys[idx]]

        height += 50

        let jobChg = {
            ...chgTemplate,
            id: ndeId,
            name: "Job: " + jobKeys[idx],
            rules: [
                /*
                 * the jobkey value allows jobs to be copied and reused just by altering this
                 * value, a new job is created in the final YAML.
                 */
                ruleString("jobkey", jobKeys[idx]),
                ruleJson("payload.jobs[msg.jobkey]", {})
            ],
            x: 40,
            y: height,
            wires: [[]]
        };

        if (Object.keys(jobObj).indexOf("name") > -1) {
            jobChg.rules.push(ruleString("payload.jobs[msg.jobkey].name", jobObj.name));
            delete jobObj.name;
        }

        ['id', 'permissions', 'runs-on'].forEach(k => {
            if (Object.keys(jobObj).indexOf(k) > -1) {
                jobChg.rules.push(ruleJson("payload.jobs[msg.jobkey]" + hashKey(k), jobObj[k]))
                delete jobObj[k];
            }
        })

        nodeCollection.push(jobChg)
        lstNde.wires[0].push(ndeId)
        lstNde = jobChg;

        /*
         * Handle the steps defined on the job. Each step uses a _reference_ to the job,
         * this allows steps to be moved and copied around within the flow _without_ 
         * having to alter the step or job.
         */
        if (jobObj.steps) {
            jobChg.rules.push(ruleJson("payload.jobs[msg.jobkey].steps", []))
            /* this becomes the reference that a step uses to reference the job it belongs to */
            jobChg.rules.push({
                "t": "set",
                "p": "job",
                "pt": "msg",
                "to": "payload.jobs[msg.jobkey]",
                "tot": "msg",
            })

            for (var jdx = 0; jdx < jobObj.steps.length; jdx++) {
                ndeId = RED.util.generateId()
                let stpObj = jobObj.steps[jdx];
                height += 50

                let stpName = (stpObj.name || stpObj.id || (stpObj.run && "Run Only") || (stpObj.uses && "Uses Only") || "No Name")

                let stepChg = {
                    ...chgTemplate,
                    id: ndeId,
                    name: "Step: " + stpName,
                    rules: [ruleJson("step", {})],
                    x: 80,
                    y: height,
                    "wires": [[]]
                };

                if (Object.keys(stpObj).indexOf("name") > -1) {
                    stepChg.rules.push(ruleString("step.name", stpObj.name))
                    delete stpObj.name
                }

                ["id", "if", "uses", "run"].forEach(k => {
                    if (Object.keys(stpObj).indexOf(k) > -1) {
                        stepChg.rules.push(ruleJson("step" + hashKey(k), stpObj[k]))
                        delete stpObj[k]
                    }
                })

                if (stpObj["with"]) {
                    stepChg.rules.push(ruleJson("step.with", {}))

                    let withKeys = Object.keys(stpObj["with"])
                    for (var kdx = 0; kdx < withKeys.length; kdx++) {
                        stepChg.rules.push(ruleJson("step.with" + hashKey(withKeys[kdx]), stpObj["with"][withKeys[kdx]]))
                    }

                    delete stpObj["with"]
                }

                Object.keys(stpObj).forEach(k => {
                    stepChg.rules.push(ruleJson("step" + hashKey(k), stpObj[k]))
                    delete stpObj[k]
                })

                /*
                 * Add step obj to steps array and clean up msg object.
                 */
                stepChg.rules.push({
                    "t": "set",
                    "p": "job.steps",
                    "pt": "msg",
                    "to": "$append( $$.job.steps, [$$.step])",
                    "tot": "jsonata"
                })

                stepChg.rules.push({
                    "t": "delete",
                    "p": "step",
                    "pt": "msg"
                })

                nodeCollection.push(stepChg)
                lstNde.wires[0].push(ndeId)
                lstNde = stepChg;
            }
            delete jobObj.steps
        }

        Object.keys(jobObj).forEach(k => {
            jobChg.rules.push(ruleJson("payload.jobs[msg.jobkey]" + hashKey(k), jobObj[k]))
            delete jobObj[k];
        })

        jobChg.rules.push({
            "t": "delete",
            "p": "jobkey",
            "pt": "msg"
        })
    }
    delete msg.payload.jobs;
}

Object.keys(msg.payload).forEach(k => {
    startHere.rules.push(ruleJson("payload" + hashKey(k), msg.payload[k]))
    delete msg.payload[k];
})

msg.payload = nodeCollection

return msg;
