    var timeout = setTimeout(function () {
        if (window.oscillators[frequency]) {
            window.oscillators[frequency].oscillator.stop(0);
            delete window.oscillators[frequency];
        }
    }, duration);

    window.oscillators[frequency] = { timeout: timeout, oscillator: oscillator };
}

