function doIt() {
  var content = payload.flowContent;

  if (payload.removeduplicates) {
    content = content.filter((elem) => {
      return RED.nodes.node(elem.id) == undefined
    });
  }

  if (content.length == 0) {
    RED.notify("No new content", {
      type: "ok",
      id: "TriggerImport",
      timeout: 2000
    });
    return;
  }
  RED.clipboard.import();

  setTimeout(() => {
    $('#red-ui-clipboard-dialog-import-text').val(
      JSON.stringify(content)
    ).trigger("paste");

    if (payload.autoimport) {
      setTimeout(() => {
        $('#red-ui-clipboard-dialog-ok').trigger('click');
      }, 435);
    }
  }, 300);
};

doIt();
