// Ensure that the event listener is only 
// set once.

var lstnName = "deployListener" + nodeid;

if (payload == "setup") {
    if (!window[lstnName]) {
        window[lstnName] = (e) => {
            node.send({
                payload: "deploy:done"
            })
        }
        RED.events.on('deploy', window[lstnName]);

        RED.notify("Deploy listener setup", {
            type: "success",
            timeout: 2500,
            id: nodeid
        })
    } else {
        RED.notify("Deploy listener already set up", {
            type: "warning",
            timeout: 2500,
            id: nodeid
        })
    }
}

// Remove listener if required

if (payload == "remove") {
    if (window[lstnName]) {
        RED.events.off('deploy', window[lstnName]);
        window[lstnName] = undefined;

        RED.notify("Deploy listener removed", {
            type: "success",
            timeout: 2500,
            id: nodeid
        })        
    } else {
        RED.notify("No Deploy listener defined", {
            type: "warning",
            timeout: 2500,
            id: nodeid
        })        
    }
}
