var feedparser = new FeedParser();

node.seen = this.context.get("seen");
node.stack = this.context.get("stack");

feedparser.on('error', function (error) { node.error(error, error); });

feedparser.on('readable', function () {
    var stream = this, article;

    while (article = stream.read()) { 
        if (!(article.guid in node.seen) || (node.seen[article.guid] !== 0 && node.seen[article.guid] != article.date.getTime())) {
            node.seen[article.guid] = article.date ? article.date.getTime() : 0;

            var dmsg = {
                ...msg,
                topic: article.origlink || article.link,
                payload: article.description,
                article: article
            };

            node.stack.unshift( dmsg )
            if (node.ignorefirst === true && node.donefirst === false) {
                // do nothing
            }
            else {
                node.send([null,null,dmsg]);
            }
        }
    }
});

feedparser.on('meta', function (meta) { });

feedparser.on('end', function () { 
    node.stack.length = msg.feedLimit || 30;
    node.stack.forEach(function(element) {
        node.send([null, element, null]);
    });
    node.send([{ ...msg, payload: node.stack }, null, null])
    node.status({fill:"green",shape:"ring",text:"done"});
});

var content = new ReadableStream.Readable();
content.push( msg.payload[1] );
content.push( null );

content.pipe(feedparser)

return null;

