var newPayload = [];

msg.payload.forEach(function(element) {
    if ( !element || !element.article ) { return }

    if ( element.article.title.match(/^User:/) ) {
        return;
    }
    if (element.article.title.match(/^User talk:/)) {
        return;
    }
    if (element.article.title.match(/^Talk:/)) {
        return;
    }
    if (element.article.author == "AAlertBot") {
        return;
    }
    newPayload.push(element);
});

msg.payload = newPayload;

return msg;

// comment links take priority
msg.payload = [
    msg.article.title,
    msg.article.comment || msg.article.comments || msg.article.link
];
return msg;

var tmp = msg.payload[ Math.floor(Math.random() * msg.payload.length )];

msg.payload = {
    title: tmp[0],
    link: tmp[1]
};

return msg;

msg.payload.forEach(function (element) {
    if (!element || !element.article) { return }

    element.article.title = element.article.title.replace('New comment by ','');
});

return msg;

// Article links take priority
msg.payload = [
    msg.article.title,
    msg.article.link || msg.article.comment || msg.article.comments
];
return msg;
